card.css.content:
/* Card Component Styles using BEM methodology */

:root {
  --color-primary: #3b82f6;
  --color-secondary: #64748b;
  --radius-md: 0.5rem;
  --shadow-md: 0 4px 6px -1px rgb(0 0 0 / 0.1);
}

.card {
  max-width: 24rem;
  border-radius: var(--radius-md);
  overflow: hidden;
  box-shadow: var(--shadow-md);
  background: white;
}

.card__image {
  width: 100%;
  height: auto;
  display: block;
}

.card__content {
  padding: 1.5rem;
}

.card__title {
  margin: 0 0 0.5rem;
  font-size: 1.25rem;
  font-weight: 600;
}

.card__description {
  color: var(--color-secondary);
  margin: 0 0 1rem;
  line-height: 1.5;
}

.card__actions {
  display: flex;
  gap: 0.5rem;
}

.btn {
  padding: 0.5rem 1rem;
  border: none;
  border-radius: var(--radius-md);
  font-weight: 500;
  cursor: pointer;
  transition: opacity 0.2s;
}

.btn:hover {
  opacity: 0.9;
}

.btn--primary {
  background: var(--color-primary);
  color: white;
}

.btn--secondary {
  background: #e2e8f0;
  color: var(--color-secondary);
}
