# Ruby Enumerable tricks and patterns

# Group and count occurrences
words = %w[apple banana apple cherry banana apple]
word_counts = words.tally
# => {"apple"=>3, "banana"=>2, "cherry"=>1}

# Transform hash values
prices = { apple: 1.50, banana: 0.75, cherry: 2.00 }
discounted = prices.transform_values { |v| (v * 0.9).round(2) }
# => {:apple=>1.35, :banana=>0.68, :cherry=>1.8}

# Partition into two arrays
numbers = (1..10).to_a
evens, odds = numbers.partition(&:even?)
# evens => [2, 4, 6, 8, 10]
# odds => [1, 3, 5, 7, 9]

# Find with default
users = [{ name: 'Alice', age: 30 }, { name: 'Bob', age: 25 }]
user = users.find(-> { { name: 'Guest', age: 0 } }) { |u| u[:name] == 'Charlie' }
# => {:name=>"Guest", :age=>0}

# Chain transformations
result = (1..100)
  .lazy
  .select { |n| n % 3 == 0 }
  .map { |n| n * 2 }
  .take(5)
  .to_a
# => [6, 12, 18, 24, 30]
