package examples

/**
 * Common monad patterns in Scala.
 */
object MonadExamples {

  // Option for null safety
  def findUser(id: Int): Option[User] = 
    users.find(_.id == id)

  def getUserEmail(id: Int): Option[String] =
    for {
      user <- findUser(id)
      email <- user.email
    } yield email

  // Either for error handling
  def divide(a: Int, b: Int): Either[String, Int] =
    if (b == 0) Left("Division by zero")
    else Right(a / b)

  def calculate(a: Int, b: Int, c: Int): Either[String, Int] =
    for {
      x <- divide(a, b)
      y <- divide(x, c)
    } yield y

  // Try for exception handling
  import scala.util.{Try, Success, Failure}

  def parseJson(json: String): Try[Map[String, Any]] =
    Try(JSON.parse(json))

  def safeOperation[A](op: => A): Try[A] = Try(op)

  // Pattern matching with case classes
  sealed trait Result[+A]
  case class Ok[A](value: A) extends Result[A]
  case class Err(message: String) extends Result[Nothing]

  def process(result: Result[Int]): String = result match {
    case Ok(value) if value > 0 => s"Positive: $value"
    case Ok(value) => s"Non-positive: $value"
    case Err(msg) => s"Error: $msg"
  }
}
