#!/usr/bin/env bash
# Useful git helper functions

# Delete merged branches (except main/master/develop)
git_clean_branches() {
    git branch --merged | grep -vE '(^\*|main|master|develop)' | xargs -r git branch -d
    echo "Cleaned merged branches"
}

# Interactive rebase on main
git_rebase_main() {
    local main_branch
    main_branch=$(git remote show origin | grep 'HEAD branch' | cut -d' ' -f5)
    git fetch origin "$main_branch"
    git rebase -i "origin/$main_branch"
}

# Show file history with diffs
git_file_history() {
    local file="$1"
    git log -p --follow -- "$file"
}

# Undo last commit (keep changes staged)
git_uncommit() {
    git reset --soft HEAD~1
    echo "Undid last commit, changes are staged"
}
