utility-types.ts.content:
/**
 * Common TypeScript utility types for everyday use
 */

// Make all properties optional recursively
type DeepPartial<T> = {
  [P in keyof T]?: T[P] extends object ? DeepPartial<T[P]> : T[P];
};

// Make specific properties required
type RequireFields<T, K extends keyof T> = T & Required<Pick<T, K>>;

// Extract non-null values from a type
type NonNullableFields<T> = {
  [P in keyof T]: NonNullable<T[P]>;
};

// Usage examples
interface User {
  id: number;
  name: string;
  email?: string;
  settings?: {
    theme: string;
    notifications: boolean;
  };
}

type PartialUser = DeepPartial<User>;
type UserWithEmail = RequireFields<User, 'email'>;

type-guards.ts.content:
/**
 * Type guard utilities for runtime type checking
 */

// Check if value is defined
function isDefined<T>(value: T | undefined | null): value is T {
  return value !== undefined && value !== null;
}

// Check if value is an object
function isObject(value: unknown): value is Record<string, unknown> {
  return typeof value === 'object' && value !== null && !Array.isArray(value);
}

// Check if value has a specific property
function hasProperty<K extends string>(
  obj: unknown,
  key: K
): obj is Record<K, unknown> {
  return isObject(obj) && key in obj;
}

// Usage
const data: unknown = { name: 'John', age: 30 };

if (hasProperty(data, 'name')) {
  console.log(data.name); // TypeScript knows 'name' exists
}
